mapper.update:
update vdlm_fragment_image
set idx = idx + #{increment}, updated_at = now()
where idx >= #{idx} and fragment_id = #{fragmentId, typeHandler=idHandler}


update vdlm_fragment_image
set idx = idx + #{increment}, updated_at = now()
where idx <= #{idx} and fragment_id = #{fragmentId, typeHandler=idHandler}

update vdlm_fragment_image
set idx = #{idx}, updated_at = now()
where id = #{id, typeHandler=idHandler}

mapper.delete:
  	delete from vdlm_fragment_image
  	where fragment_id = #{fragmentId, typeHandler=idHandler}

  		id in

	       #{id}
