mapper.select:
select 

from vdlm_fragment
where id = #{id, typeHandler=idHandler}

mapper.update:
update vdlm_fragment
set idx = idx + #{increment}, updated_at = now()
where idx >= #{idx} and shop_id = #{shopId, typeHandler=idHandler}


update vdlm_fragment
set idx = idx + #{increment}, updated_at = now()
where idx <= #{idx} and shop_id = #{shopId, typeHandler=idHandler}

update vdlm_fragment
set idx = #{idx}, updated_at = now()
where id = #{id, typeHandler=idHandler}
